import React, { useState, setState } from 'react';
import './Dashboard/Dashboard.css';

function Form(props) {

  const[values, setValues] = useState({
      gym: '',
      climbType: '',
      grade: '',
      rating: '',
  })


  const handleGradeInputChange = (event) => {
      setValues({...values, grade: event.target.value})
  }

  const[submitted, setSubmitted] = useState(false)

  const handleSubmit = (event) => {
    event.preventDefault();
    setValues({...values, gym: event.target.value, climbType: event.target.value, grade: event.target.value, rating: event.target.value})
    props.onSubmit({
        gym: values.gym,
        climbType: values.climbType,
        grade: values.grade,
        rating: values.rating
      });
      setSubmitted(true);

  }

  return (

      <div className='form-container'>
          <form className='form' onSubmit={handleSubmit}>
              {submitted ? <div className='success-message'>Submitted!</div> : null}
              <h1 className='dash-title'>Submit a Send</h1>
              <select id='gym' className='form-field' name='gym'>
                  <option value='bkb-wl'>BKB West Loop</option>
                  <option value='bkb-lp'>BKB Lincoln Park</option>
                  <option value='fa-a'>FA Avondale</option>
              </select>
              <select id='climbType' className='form-field' name='climbType'>
                  <option value='boulder'>Boulder</option>
                  <option value='top-rope'>Top Rope</option>
                  <option value='lead'>Lead</option>
              </select>

              <input
                  onChange={handleGradeInputChange}
                  value={values.grade}
                  className='form-field'
                  type='text'
                  placeholder='Grade'
                  name='grade'
              />
              <select id='rating' className='form-field' name='rating'>
                  <option value='1-star'>1 Star</option>
                  <option value='2-stars'>2 Stars</option>
                  <option value='3-stars'>3 Stars</option>
                  <option value='4-stars'>4 Stars</option>
                  <option value='5-stars'>5 Stars</option>
              </select>

              <button className='form-field' type='submit'>
                  Submit
              </button>

          </form>
        </div>




  )
}

export default Form;
